// Inputs esperados:
// FILENAME   -> ex: "_LMpN6o26E0_00_00_00_00_00_06.mp4" (em /home/node/processing)
// V_SAT      -> satura√ß√£o (0‚Äì2, padr√£o 0.6)
// V_CONTRAST -> contraste (0.5‚Äì2, padr√£o 1.05)
// V_BRIGHT   -> brilho (-1‚Äì1, padr√£o 0)
// V_GRAIN    -> intensidade do gr√£o (0‚Äì100, padr√£o 20)
// V_FLAGS    -> flags do noise ("u+t" padr√£o)      // u=uniforme, g=gaussiano, p=positivo, t=temporal
// V_SEED     -> semente do ru√≠do (0..2^32-1, padr√£o 42)
// V_VIGN     -> par√¢metro do vignette (string opcional, ex: "PI/4")

const file = $json.FILENAME;
const basePath = $('START').first().json.BASE_PATH;

const V_SAT      = Number($json.V_SAT ?? 0.6);
const V_CONTRAST = Number($json.V_CONTRAST ?? 1.05);
const V_BRIGHT   = Number($json.V_BRIGHT ?? 0);
const V_GRAIN    = Number($json.V_GRAIN ?? 20);
const V_FLAGS    = ($json.V_FLAGS ?? 'u+t').toString();
const V_SEED     = Number($json.V_SEED ?? 42);
const V_VIGN     = $json.V_VIGN;

const BASE = '/home/node/processing/'+basePath;

// Nome de sa√≠da
const safe = s => s.replace(/\.[A-Za-z0-9]+$/,'').replace(/[^A-Za-z0-9_\-]/g,'_');
const outFile = `${safe(file)}_VINTAGE_sat${V_SAT}_ct${V_CONTRAST}_gr${V_GRAIN}.mp4`;

// Monta a vinheta
const vignettePart = V_VIGN ? `vignette=${V_VIGN}` : `vignette`;

// Cadeia de filtros
const vf =
  `eq=contrast=${V_CONTRAST}:brightness=${V_BRIGHT},` +
  `hue=s=${V_SAT},` +
  `noise=alls=${V_GRAIN}:allf=${V_FLAGS}:all_seed=${V_SEED},` +
  `${vignettePart},` +
  `format=yuv420p`;

const cmd =
  `ffmpeg -y -i "${BASE}/${file}" ` +
  `-vf "${vf}" ` +
  `-c:v libx264 -preset veryfast -crf 20 -movflags +faststart ` +
  `-c:a copy ` +
  `"${BASE}/${outFile}"`;

return {
  command: cmd,
  outputFile: outFile
};
