// Code node (Run Once for All Items)

return items.map((item) => {
  const json = item.json ?? {};
  const raw = json.stdout ?? item.stdout ?? "";

  let videoMetaData = null;

  if (typeof raw === "string") {
    const s = raw.trim();
    try {
      videoMetaData = JSON.parse(s);
    } catch (e1) {
      // Se tiver texto extra, tenta isolar o primeiro bloco JSON
      const start = s.indexOf("{");
      const end = s.lastIndexOf("}");
      if (start !== -1 && end !== -1 && end > start) {
        try {
          videoMetaData = JSON.parse(s.slice(start, end + 1));
        } catch (e2) {
          videoMetaData = { ok: false, parseError: String(e2), rawPreview: s.slice(0, 500) };
        }
      } else {
        videoMetaData = { ok: false, parseError: "Nenhum JSON detectado em stdout", rawPreview: s.slice(0, 500) };
      }
    }
  } else if (raw && typeof raw === "object") {
    // Caso raro: stdout já veio como objeto
    videoMetaData = raw;
  }

  return {
    json: {
      ...json,
      videoMetaData,
    },
  };
});
