// n8n Code node (JavaScript)
// Mode: "Run Once for All Items"

// Chance de "passar" a entrada: 70%
// Chance de "zerar data": 30%
const PASS_PROB = 0.70;

// Os itens que chegaram neste node
const inputItems = $input.all();

// Sorteio 0..1
const r = Math.random();

// Se cair nos 30%, devolve data vazio
if (r >= PASS_PROB) {
  // Mantém o mesmo formato do seu exemplo: [{ data: [...] }]
  // Se sua entrada tiver múltiplos items, você pode escolher um comportamento.
  // Aqui, vou devolver 1 item com data vazio (como seu exemplo).
  return [
    {
      json: {
        data: []
      },
    },
  ];
}

// Se cair nos 70%, devolve exatamente o que entrou (sem alterar)
return inputItems;
