// Lê o retorno cru (ajuste a origem se for outro campo)
let raw = $json.stdout ?? $json.data ?? $json.raw ?? '';

if (typeof raw !== 'string') {
  raw = String(raw);
}

// Se vier como string JSON (começa com aspas), tenta desserializar uma vez
if (raw.startsWith('"') && raw.endsWith('"')) {
  try { raw = JSON.parse(raw); } catch (e) { /* ok, segue em frente */ }
}

// Normaliza: espaços escapados e barras
let s = raw.replace(/\\ /g, ' ');

// Extrai cada objeto {...} do array
const objMatches = s.match(/\{[^}]*\}/g) || [];
const numberKeys = new Set(['fileSize', 'fileSizeHuman']);

const out = [];

for (const chunk of objMatches) {
  const rec = {};
  const pairRe = /"([^"]+)":([^,}]+)/g;
  let m;

  while ((m = pairRe.exec(chunk)) !== null) {
    const key = m[1];
    let val = m[2].trim();

    // Remove aspas se existirem
    if (val.startsWith('"') && val.endsWith('"')) {
      val = val.slice(1, -1);
    }

    // Desescapa restos comuns
    val = val.replace(/\\\\/g, '\\')
             .replace(/\\n/g, '\n')
             .replace(/\\t/g, '\t');

    if (numberKeys.has(key)) {
      // Garante número (remove qualquer sufixo estranho)
      const num = Number(val.replace(/[^\d.-]/g, ''));
      rec[key] = Number.isFinite(num) ? num : null;
    } else {
      // Arruma valores "especiais"
      if (key === 'created' && (val === '-' || val === 'w' || val === '')) {
        rec[key] = null;
      } else {
        rec[key] = val;
      }
    }
  }

  // Campo auxiliar: caminho completo
  if (rec.directory && rec.fileName) {
    rec.file = `${rec.fileName}`;
  }

  out.push({ json: rec });
}

// Retorna um item por arquivo
return out;

// n8n Code (Node.js)
// Lê via $input; opcional em qualquer item: DEST_DIR, OUTPUT_FILE_NAME, FADE_SEC (default 2s)

const all = $input.all().map(i => i.json);
if (!all.length) throw new Error('Nenhum item de entrada foi recebido.');

let filesList = [];
if (all.length > 1 || (all[0] && all[0].file && all[0].directory)) filesList = all;
else if (Array.isArray(all[0]?.FILES)) filesList = all[0].FILES;
else throw new Error('Esperava itens com {directory, file} ou um objeto com FILES[].');

const firstWithDest = all.find(o => o.DEST_DIR) || {};
const firstWithName = all.find(o => o.OUTPUT_FILE_NAME) || {};
const firstWithFade = all.find(o => o.FADE_SEC !== undefined) || {};

function normDir(d){ return String(d || '').replace(/\/+$/,''); }
function quote(s){ return `"${String(s).replace(/"/g,'\\"')}"`; }

let DEST_DIR = normDir(firstWithDest.DEST_DIR);
if (!DEST_DIR) DEST_DIR = normDir(filesList[0]?.directory);
if (!DEST_DIR) throw new Error('DEST_DIR não informado e não foi possível inferir do primeiro arquivo.');

function tailIndex(name){ const m = String(name).match(/_(\d+)\.mp4$/i); return m ? parseInt(m[1],10) : null; }
const ordered = [...filesList].sort((a,b)=>{
  const fa = a.file || a.fileName || '';
  const fb = b.file || b.fileName || '';
  const ia = tailIndex(fa), ib = tailIndex(fb);
  if (ia != null && ib != null && ia !== ib) return ia - ib;
  if (ia != null && ib == null) return -1;
  if (ia == null && ib != null) return 1;
  return fa.localeCompare(fb);
});

const inputs = ordered.map(o => {
  const dir = normDir(o.directory);
  const name = o.file || o.fileName;
  if (!dir || !name) throw new Error('Cada item precisa de "directory" e "file".');
  return `${dir}/${name}`;
});

// Nome de saída com prefixo DAY_SUMMARY_
const PREFIX = 'DAY_SUMMARY_';
const rawOutName = (firstWithName.OUTPUT_FILE_NAME || ordered[0]?.file || `MERGED_${Date.now()}.mp4`);
const cleanedOutName = String(rawOutName).replace(/[\\\/:*?"<>|]/g,'_');
const outputFile = cleanedOutName.startsWith(PREFIX) ? cleanedOutName : `${PREFIX}${cleanedOutName}`;
const finalOut = `${DEST_DIR}/${outputFile}`;

// Fade
const fadeSec = Number(firstWithFade.FADE_SEC);
const FADE_SEC = Number.isFinite(fadeSec) && fadeSec > 0 ? fadeSec : 2;

// Inputs quotados pro shell
const inputsQuoted = inputs.map(p => `"${p.replace(/"/g,'\\"')}"`).join(' ');

// Script POSIX (sem wrapper). ATENÇÃO: ${OUT_FADE} precisa ser literal → ${'${OUT_FADE}'}
const script = `
set -e
FADE_SEC=${FADE_SEC}
DEST_DIR=${quote(DEST_DIR)}
FINAL_OUT=${quote(finalOut)}

mkdir -p "$DEST_DIR"

TMPDIR="$DEST_DIR/.mergefade_$(date +%s)_$$"
mkdir -p "$TMPDIR"
trap 'rm -rf "$TMPDIR"' EXIT

LIST="$TMPDIR/list.txt"
: > "$LIST"

idx=0
for SRC in ${inputsQuoted}; do
  DUR=$(ffprobe -v error -show_entries format=duration -of default=nk=1:nw=1 "$SRC" | tr -d '\\r')
  [ -z "$DUR" ] && DUR=0
  OUT_FADE=$(awk -v d="$DUR" -v f="$FADE_SEC" 'BEGIN{ s=d-f; if(s<0) s=0; printf "%.6f", s }')

  HAS_AUDIO=$(ffprobe -v error -select_streams a -show_entries stream=index -of csv=p=0 "$SRC" | head -n1 || true)
  PART="$TMPDIR/part_$(printf "%03d" "$idx").mp4"

  if [ -n "$HAS_AUDIO" ]; then
    ffmpeg -y -i "$SRC" \
      -vf "fps=30,format=yuv420p,setsar=1,fade=t=in:st=0:d=${FADE_SEC},fade=t=out:st=${'${OUT_FADE}'}:d=${FADE_SEC}" \
      -af "aformat=sample_fmts=fltp:sample_rates=44100:channel_layouts=stereo,afade=t=in:st=0:d=${FADE_SEC},afade=t=out:st=${'${OUT_FADE}'}:d=${FADE_SEC}" \
      -c:v libx264 -profile:v high -level:v 4.1 -pix_fmt yuv420p -movflags +faststart \
      -c:a aac -b:a 192k \
      "$PART"
  else
    SIL="$TMPDIR/sil_$(printf "%03d" "$idx").wav"
    SR=44100
    ffmpeg -y -f lavfi -i anullsrc=channel_layout=stereo:sample_rate=$SR -t "$DUR" "$SIL"

    ffmpeg -y -i "$SRC" -i "$SIL" \
      -map 0:v:0 -map 1:a:0 \
      -vf "fps=30,format=yuv420p,setsar=1,fade=t=in:st=0:d=${FADE_SEC},fade=t=out:st=${'${OUT_FADE}'}:d=${FADE_SEC}" \
      -af "aformat=sample_fmts=fltp:sample_rates=44100:channel_layouts=stereo,afade=t=in:st=0:d=${FADE_SEC},afade=t=out:st=${'${OUT_FADE}'}:d=${FADE_SEC}" \
      -c:v libx264 -profile:v high -level:v 4.1 -pix_fmt yuv420p -movflags +faststart \
      -c:a aac -b:a 192k \
      "$PART"
  fi

  printf "file '%s'\\n" "$PART" >> "$LIST"
  idx=$((idx+1))
done

ffmpeg -y -f concat -safe 0 -i "$LIST" -c copy -movflags +faststart "$FINAL_OUT"
echo "OK: $FINAL_OUT"
`.trim() + '\n';

return {
  command: script,              // <— sem "sh -c"
  inputs,
  outputFile,
  finalOutput: finalOut,
  debug: {
    fadeSec: FADE_SEC,
    count: inputs.length,
    order: ordered.map(o=>o.file || o.fileName),
    dest: DEST_DIR,
    note: 'Escapado ${OUT_FADE} dentro do template JS; script cru no Execute Command'
  }
};

