(() => {
  const zone = 'America/Sao_Paulo';
  const now = $now.setZone(zone);
  const plus1 = now.plus({ hours: 2 });
  const today23 = now.set({ hour: 23, minute: 0, second: 0, millisecond: 0 });

  // Se já passou de 23:00, use now+1h; senão, escolha o menor entre (now+1h) e (hoje 23:00)
  const target = today23 <= now ? plus1 : (plus1 < today23 ? plus1 : today23);

  return target.toFormat("yyyy-LL-dd'T'HH:mm:ss");
})()
}}
