/**
 * n8n Code node (JavaScript)
 * Output: { type, content, participantId, ... , image, imageRequestPayload, instance, serverUrl, apiKey }
 */

function normalizeType(messageType, messageObj) {
  const mt = (messageType || '').toLowerCase();

  if (mt === 'conversation') return 'text';
  if (mt === 'audiomessage') return 'audio';
  if (mt === 'imagemessage') return 'image';

  if (messageObj?.conversation != null) return 'text';
  if (messageObj?.audioMessage != null) return 'audio';
  if (messageObj?.imageMessage != null) return 'image';

  return 'unknown';
}

function extractContent(type, messageObj) {
  if (!messageObj || typeof messageObj !== 'object') return '';

  if (type === 'text') {
    const text = messageObj.conversation;
    return typeof text === 'string' ? text : '';
  }

  if (type === 'audio') {
    const url = messageObj.audioMessage?.url;
    return typeof url === 'string' ? url : '';
  }

  if (type === 'image') {
    const url = messageObj.imageMessage?.url;
    return typeof url === 'string' ? url : '';
  }

  const url = messageObj?.audioMessage?.url ?? messageObj?.imageMessage?.url;
  return typeof url === 'string' ? url : '';
}

/**
 * Evolution webhook traz alguns campos binários como objeto {0:...,1:...}
 * Converte para base64 (string).
 */
function objectBytesToBase64(maybeObj) {
  if (!maybeObj || typeof maybeObj !== 'object') return maybeObj;
  // já é Buffer?
  if (Buffer.isBuffer(maybeObj)) return maybeObj.toString('base64');

  // precisa ser um objeto com chaves numéricas
  const keys = Object.keys(maybeObj);
  if (!keys.length) return maybeObj;
  const looksNumeric = keys.every((k) => String(+k) === k);
  if (!looksNumeric) return maybeObj;

  const bytes = keys
    .map((k) => Number(k))
    .sort((a, b) => a - b)
    .map((k) => maybeObj[String(k)]);

  return Buffer.from(bytes).toString('base64');
}

function normalizeImageMessage(imageMessage) {
  if (!imageMessage || typeof imageMessage !== 'object') return null;

  // shallow clone
  const img = { ...imageMessage };

  // campos que normalmente vêm como bytes-obj e é melhor enviar em base64
  const byteFields = [
    'mediaKey',
    'fileSha256',
    'fileEncSha256',
    'jpegThumbnail',
    'scansSidecar',
    'midQualityFileSha256',
  ];

  for (const f of byteFields) {
    if (img[f] != null) {
      img[f] = objectBytesToBase64(img[f]);
    }
  }

  // fileLength às vezes vem como {low, high...}; transforma em number simples
  if (img.fileLength && typeof img.fileLength === 'object' && img.fileLength.low != null) {
    img.fileLength = Number(img.fileLength.low);
  }

  // mediaKeyTimestamp idem
  if (
    img.mediaKeyTimestamp &&
    typeof img.mediaKeyTimestamp === 'object' &&
    img.mediaKeyTimestamp.low != null
  ) {
    img.mediaKeyTimestamp = Number(img.mediaKeyTimestamp.low);
  }

  return img;
}

return items.map((item) => {
  const body = item.json?.body ?? {};
  const data = body?.data ?? {};
  const key = data?.key ?? {};
  const messageType = data?.messageType;
  const message = data?.message;

  const type = normalizeType(messageType, message);
  const content = extractContent(type, message);

  const instance = body?.instance ?? null; // no seu payload: "n8n"
  const serverUrl = body?.server_url ?? null; // no seu payload: "http://localhost:8080"
  const apiKey = body?.apikey ?? null;

  const rawImageMessage = message?.imageMessage;
  const imageMessage = normalizeImageMessage(rawImageMessage);

  // payload pronto pra usar no HTTP Request (pegar Base64)
  const imageRequestPayload =
    type === 'image' && imageMessage
      ? {
          message: {
            key: {
              remoteJid: key?.remoteJid,
              id: key?.id,
            },
            message: {
              imageMessage: imageMessage,
            },
          },
        }
      : null;

  return {
    json: {
      type,
      content,
      participantId: key?.participant || null,

      // extras úteis (mantidos)
      messageId: key?.id,
      remoteJid: key?.remoteJid,
      fromMe: key?.fromMe,
      sender: body?.sender,
      timestamp: data?.messageTimestamp,

      // ✅ novos campos pra baixar/pegar base64 da imagem depois
      instance,
      serverUrl,
      apiKey,

      // ✅ pacote do media (já normalizado pra base64 quando necessário)
      image: imageMessage
        ? {
            url: imageMessage.url,
            directPath: imageMessage.directPath,
            mimetype: imageMessage.mimetype,
            fileLength: imageMessage.fileLength,
            width: imageMessage.width,
            height: imageMessage.height,
            mediaKey: imageMessage.mediaKey,
            fileSha256: imageMessage.fileSha256,
            fileEncSha256: imageMessage.fileEncSha256,
          }
        : null,

      // ✅ payload pronto pra mandar no HTTP Request node
      imageRequestPayload,
    },
  };
});

return items.map((item, idx) => {
  const dataUrl = item.json.base64 ?? '';
  if (!dataUrl) return item;

  // Aceita tanto "data:...;base64,..." quanto base64 puro
  const match = /^data:([^;]+);base64,(.*)$/i.exec(dataUrl);
  const mimeType = (match?.[1] || item.json.mimeType || 'application/octet-stream').trim();
  const base64 = match?.[2] || dataUrl;

  const buffer = Buffer.from(base64, 'base64');

  // Mime → extensão (cobre WhatsApp + mp3)
  const mimeToExt = {
    'image/png': 'png',
    'image/jpeg': 'jpg',
    'image/jpg': 'jpg',
    'image/webp': 'webp',

    'audio/mpeg': 'mp3',
    'audio/mp3': 'mp3',
    'audio/ogg': 'ogg',
    'audio/opus': 'opus',
    'audio/ogg; codecs=opus': 'ogg', // às vezes vem assim
    'audio/wav': 'wav',
    'audio/x-wav': 'wav',
    'audio/mp4': 'm4a',
    'audio/aac': 'aac',
    'audio/webm': 'webm',

    'video/mp4': 'mp4',
    'application/pdf': 'pdf',
  };

  // Normaliza mimeType para tentar bater no mapa mesmo se vier com parâmetros
  const mimeKey = mimeType.toLowerCase();
  const mimeKeyNoParams = mimeKey.split(';')[0].trim();

  let ext =
    mimeToExt[mimeKey] ||
    mimeToExt[mimeKeyNoParams] ||
    'bin';

  // Se o Evolution mandou mimeType esquisito mas o mediaType diz audioMessage,
  // dá pra forçar um fallback mais inteligente:
  const mediaType = item.json.mediaType;
  if (ext === 'bin' && mediaType === 'audioMessage') {
    // WhatsApp quase sempre é OGG/OPUS quando é PTT
    ext = 'ogg';
  }

  // Nome do arquivo baseado no tipo
  const prefix =
    mimeKeyNoParams.startsWith('image/') ? 'imagem' :
    mimeKeyNoParams.startsWith('audio/') ? 'audio' :
    mimeKeyNoParams.startsWith('video/') ? 'video' :
    'arquivo';

  item.binary = item.binary ?? {};

  // Mantém o binary field "data" (igual ao teu), agora genérico
  item.binary.data = {
    data: buffer.toString('base64'),
    mimeType: mimeKeyNoParams,   // sem parâmetros (mais limpo pro n8n)
    fileName: `${prefix}-${idx}.${ext}`,
  };

  return item;
});
