// Code node (JavaScript) — Run once for all items
// Seleciona 1 item aleatório a partir de $input.all()
// Preserva json e binary do item escolhido

const items = $input.all();
if (!items || items.length === 0) {
  throw new Error('Sem itens de entrada para sortear.');
}

// (Opcional) Filtrar por algum critério antes do sorteio, ex.: só vídeos MP4
// const pool = items.filter(i => i.json?.fileExtension?.toLowerCase() === 'mp4');
const pool = items;

if (pool.length === 0) {
  throw new Error('Nenhum item válido após o filtro.');
}

const idx = Math.floor(Math.random() * pool.length);
const chosen = pool[idx];

// (Opcional) Marcar o índice sorteado
chosen.json = { ...chosen.json, _randomIndex: idx };

// Retorna exatamente 1 item para o próximo node
return [chosen];
