// Code node (JavaScript) — Run once for all items
// Seleciona 1 item aleatório a partir de $input.all()
// Preserva json e binary do item escolhido

const items = $input.all();
if (!items || items.length === 0) {
  throw new Error('Sem itens de entrada para sortear.');
}

// (Opcional) Filtrar por algum critério antes do sorteio, ex.: só vídeos MP4
// const pool = items.filter(i => i.json?.fileExtension?.toLowerCase() === 'mp4');
const pool = items;

if (pool.length === 0) {
  throw new Error('Nenhum item válido após o filtro.');
}

const idx = Math.floor(Math.random() * pool.length);
const chosen = pool[idx];

// (Opcional) Marcar o índice sorteado
chosen.json = { ...chosen.json, _randomIndex: idx };

// Retorna exatamente 1 item para o próximo node
return [chosen];

return $input.first().json.output.map((item, index) => ({
  ...item,
  idx: index,
  timeStamp: Date.now() + index * 1000,
}));

function normalizeThumbTexts(t) {
  if (Array.isArray(t)) return t;
  if (typeof t === 'string' && t.trim()) return [t.trim()];
  return [];
}

const mapped = items.map((item) => {
  const o = item?.json?.output ?? {};

  return {
    videoCutStartTime: Number(o.videoCutStartTime ?? 0),
    videoCutEndTime:   Number(o.videoCutEndTime ?? 0),
    videoCutTitle:     String(o.videoCutTitle ?? ''),
    videoCutDescription: String(o.videoCutDescription ?? ''),
    videoHardHook:     String(o.videoHardHook ?? ''),
    videoTags:         Array.isArray(o.videoTags) ? o.videoTags : [],
    videoUrl:          String(o.videoUrl ?? ''),
    thumbTexts:        normalizeThumbTexts(o.thumbTexts),
    playlist:          o.playlist ? { nome: o.playlist.nome, id: o.playlist.id } : undefined,    
    idx:               o.idx ?? null,
    timeStamp:         o.timeStamp ?? null,
  };
});
return [
  {
    json: {
      output: mapped
    }
  }
];
